        var fields = "";
        if (definition) {
            if (definition.indexDefinitions) {
                fields += " [";
                definition.indexDefinitions.forEach(function (elem, idx, array) {
                    if (idx > 0)fields += ","
                    fields += elem.field;
                });
                fields += "]";
            } else {
                fields += "[" + definition.field + "]";
            }
        }
        return fields;
